INSERT INTO `mybb_announcements` (`aid`, `fid`, `uid`, `subject`, `message`, `startdate`, `enddate`, `allowhtml`, `allowmycode`, `allowsmilies`) VALUES
(4, -1, 1, 'Global Forum Rules', '<ul><li> No flaming or derogatory remarks, directly or through insinuation.</li>\r\n<li>  No discussion, sharing or referencing illegal software such as hacks, keygen, cracks and pirated software.</li>\r\n<li>  No offensive contents, including but not limited to, racism, gore or pornography.</li>\r\n<li>  No excessive spam, <i>i.e.</i> copious one liners in a short period of time or typing with all caps.</li>\r\n<li>  No trolling, including but not limited to, flame incitation, user provocation or false information distribution.</li>\r\n<li>  No link spamming or signature advertisements for content not specific to DotU.</li>\r\n<li>  No discussion, sharing or referencing unauthorized beta maps.</li>\r\n<li>No double posts - use the edit button.</li>\r\n</ul>', 1335421860, 0, 1, 1, 1),
(5, 67, 1, 'Rules for Programming Section', '<ul>\r\n<li>Only programming related discussions allowed.</li>\r\n<li>No hack/crack/virus programming discussions.</li>\r\n<li>Sharing of non-open source code is strictly prohibited unless you own it or have permission to do so.</li>\r\n</ul>\r\n', 1335783600, 0, 1, 1, 1);
