INSERT INTO `mybb_settings` (`sid`, `name`, `title`, `description`, `optionscode`, `value`, `disporder`, `gid`, `isdefault`) VALUES
(1, 'enablecalendar', 'Enable Calendar Functionality', 'If you wish to disable the calendar on your board, set this option to no.', 'yesno', '1', 1, 1, 1),
(2, 'bbcodeinserter', 'Clickable MyCode Editor', 'Set this option to On to show the clickable code buttons editor on posting pages. Switching this off will also disable the Smilies Inserter.', 'onoff', '1', 1, 2, 1),
(3, 'smilieinserter', 'Clickable Smilies Inserter', 'Clickable smilies will appear on the posting pages if this option is set to ''on''.', 'onoff', '1', 1, 2, 1),
(4, 'smilieinsertertot', 'No. of Smilies to show', 'Enter the total number of smilies to show on the clickable smilie inserter.', 'text', '20', 2, 2, 1),
(5, 'smilieinsertercols', 'No. of Smilie Cols to Show', 'Enter the number of columns you wish to show on the clickable smilie inserter.', 'text', '4', 3, 2, 1),
(6, 'cplanguage', 'Control Panel Language', 'The language of the control panel.', 'adminlanguage', 'english', 1, 3, 1),
(7, 'cpstyle', 'Control Panel Style', 'The Default style that the control panel will use. Styles are inside the styles folder. A folder name inside that folder becomes the style title and style.css inside the style title folder is the css style file.', 'cpstyle', 'default', 2, 3, 1),
(8, 'maxloginattempts', 'Max Number of Login Attempts', 'The max number of attempts to login before being locked out. Set to 0 to disable.', 'text', '5', 3, 3, 1),
(9, 'loginattemptstimeout', 'Login Attempts Timeout', 'If the person trying to login reaches the max number of attempts, how long should they have to wait before being able to login again? (Set in minutes)', 'text', '15', 4, 3, 1),
(10, 'dateformat', 'Date Format', 'The format of the dates used on the forum. This format uses the PHP date() function. We recommend not changing this unless you know what you''re doing.', 'text', 'd-m-Y', 1, 4, 1),
(11, 'timeformat', 'Time Format', 'The format of the times used on the forum. This format uses PHP''s date() function. We recommend not changing this unless you know what you''re doing.', 'text', 'h:i A', 2, 4, 1),
(12, 'regdateformat', 'Registered Date Format', 'The format used on showthread where it shows when the user registered.', 'text', 'M Y', 3, 4, 1),
(13, 'timezoneoffset', 'Default Timezone Offset', 'Here you can set the default timezone offset for guests and members using the default offset.', 'php\r\n<select name=\\"upsetting[{$setting[''name'']}]\\">\r\n<option value=\\"-12\\" ".($setting[''value''] == -12?"selected=\\"selected\\"":"").">GMT -12:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, -12).")</option>\r\n<option value=\\"-11\\" ".($setting[''value''] == -11?"selected=\\"selected\\"":"").">GMT -11:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, -11).")</option>\r\n<option value=\\"-10\\" ".($setting[''value''] == -10?"selected=\\"selected\\"":"").">GMT -10:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, -10).")</option>\r\n<option value=\\"-9\\" ".($setting[''value''] == -9?"selected=\\"selected\\"":"").">GMT -9:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, -9).")</option>\r\n<option value=\\"-8\\" ".($setting[''value''] == -8?"selected=\\"selected\\"":"").">GMT -8:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, -8).")</option>\r\n<option value=\\"-7\\" ".($setting[''value''] == -7?"selected=\\"selected\\"":"").">GMT -7:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, -7).")</option>\r\n<option value=\\"-6\\" ".($setting[''value''] == -6?"selected=\\"selected\\"":"").">GMT -6:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, -6).")</option>\r\n<option value=\\"-5\\" ".($setting[''value''] == -5?"selected=\\"selected\\"":"").">GMT -5:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, -5).")</option>\r\n<option value=\\"-4\\" ".($setting[''value''] == -4?"selected=\\"selected\\"":"").">GMT -4:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, -4).")</option>\r\n<option value=\\"-3.5\\" ".($setting[''value''] == -3.5?"selected=\\"selected\\"":"").">GMT -3:30 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, -3.5).")</option>\r\n<option value=\\"-3\\" ".($setting[''value''] == -3?"selected=\\"selected\\"":"").">GMT -3:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, -3).")</option>\r\n<option value=\\"-2\\" ".($setting[''value''] == -2?"selected=\\"selected\\"":"").">GMT -2:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, -2).")</option>\r\n<option value=\\"-1\\" ".($setting[''value''] == -1?"selected=\\"selected\\"":"").">GMT -1:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, -1).")</option>\r\n<option value=\\"0\\" ".($setting[''value''] == 0?"selected=\\"selected\\"":"").">GMT (".my_date($mybb->settings[''timeformat''], TIME_NOW, 0).")</option>\r\n<option value=\\"+1\\" ".($setting[''value''] == 1?"selected=\\"selected\\"":"").">GMT +1:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, 1).")</option>\r\n<option value=\\"+2\\" ".($setting[''value''] == 2?"selected=\\"selected\\"":"").">GMT +2:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, 2).")</option>\r\n<option value=\\"+3\\" ".($setting[''value''] == 3?"selected=\\"selected\\"":"").">GMT +3:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, 3).")</option>\r\n<option value=\\"+3.5\\" ".($setting[''value''] == 3.5?"selected=\\"selected\\"":"").">GMT +3:30 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, 3.5).")</option>\r\n<option value=\\"+4\\" ".($setting[''value''] == 4?"selected=\\"selected\\"":"").">GMT +4:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, 4).")</option>\r\n<option value=\\"+4.5\\" ".($setting[''value''] == 4.5?"selected=\\"selected\\"":"").">GMT +4:30 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, 4.5).")</option>\r\n<option value=\\"+5\\" ".($setting[''value''] == 5?"selected=\\"selected\\"":"").">GMT +5:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, 5).")</option>\r\n<option value=\\"+5.5\\" ".($setting[''value''] == 5.5?"selected=\\"selected\\"":"").">GMT +5:30 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, 5.5).")</option>\r\n<option value=\\"+5.75\\" ".($setting[''value''] == 5.75?"selected=\\"selected\\"":"").">GMT +5:45 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, 5.75).")</option>\r\n<option value=\\"+6\\" ".($setting[''value''] == 9?"selected=\\"selected\\"":"").">GMT +6:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, 6).")</option>\r\n<option value=\\"+7\\" ".($setting[''value''] == 7?"selected=\\"selected\\"":"").">GMT +7:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, 7).")</option>\r\n<option value=\\"+8\\" ".($setting[''value''] == 8?"selected=\\"selected\\"":"").">GMT +8:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, 8).")</option>\r\n<option value=\\"+9\\" ".($setting[''value''] == 9?"selected=\\"selected\\"":"").">GMT +9:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, 9).")</option>\r\n<option value=\\"+9.5\\" ".($setting[''value''] == 9.5?"selected=\\"selected\\"":"").">GMT +9:30 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, 9.5).")</option>\r\n<option value=\\"+10\\" ".($setting[''value''] == 10?"selected=\\"selected\\"":"").">GMT +10:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, 10).")</option>\r\n<option value=\\"+10.5\\" ".($setting[''value''] == 10.5?"selected=\\"selected\\"":"").">GMT +10:30 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, 10.5).")</option>\r\n<option value=\\"+11\\" ".($setting[''value''] == 11?"selected=\\"selected\\"":"").">GMT +11:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, 11).")</option>\r\n<option value=\\"+12\\" ".($setting[''value''] == 12?"selected=\\"selected\\"":"").">GMT +12:00 Hours (".my_date($mybb->settings[''timeformat''], TIME_NOW, 12).")</option>\r\n</select>', '+1', 4, 4, 1),
(14, 'dstcorrection', 'Day Light Savings Time', 'If times are an hour out above and your timezone is selected correctly, enable day light savings time correction.', 'yesno', '0', 5, 4, 1),
(15, 'threadsperpage', 'Threads Per Page', 'The number of threads to display per page on the forum display', 'text', '20', 1, 5, 1),
(16, 'hottopic', 'Replies For Hot Topic', 'The number of replies that is needed for a topic to be considered ''hot''.', 'text', '20', 2, 5, 1),
(17, 'hottopicviews', 'Views For Hot Topic', 'The number of views a thread can have before it is considered ''hot''.', 'text', '150', 3, 5, 1),
(18, 'usertppoptions', 'User Selectable Threads Per Page', 'If you would like to allow users to select how many threads per page are shown in a forum, enter the options they should be able to select separated with commas. If this is left blank they will not be able to choose how many threads are shown per page.', 'text', '10,20,25,30,40,50', 4, 5, 1),
(19, 'dotfolders', 'Use ''dot'' Icons', 'Do you want to show dots on the thread indicators of threads users have participated in.', 'yesno', '1', 5, 5, 1),
(20, 'browsingthisforum', 'Users Browsing this Forum', 'Here you can turn off the ''users browsing this forum'' feature.', 'onoff', '1', 7, 5, 1),
(21, 'announcementlimit', 'Announcements Limit', 'The number of forum announcements to  show in the thread listing on the forum display pages. Set to 0 to disable announcements altogether.', 'text', '2', 8, 5, 1),
(22, 'readparentforums', 'Attempt to Mark Parent Forums as Read', 'When set to yes, this setting will attempt to mark the parent forums of a sub-forum as read if there are no more unread posts. Please note that this setting can lead to a decrease in performance and multiple database queries and therefore must be treated as experimental. See the <a href="http://docs.mybb.com/165.html">MyBB Docs</a> for more information regarding this change.', 'yesno', '0', 9, 5, 1),
(23, 'showdescriptions', 'Show Forum Descriptions?', 'This option will allow you to turn off showing the descriptions for forums.', 'yesno', '1', 1, 6, 1),
(24, 'subforumsindex', 'Subforums to show on Index listing', 'The number of subforums that you wish to show inside forums on the index and forumdisplay pages. Set to 0 to not show the subforum list', 'text', '2', 2, 6, 1),
(25, 'subforumsstatusicons', 'Show Subforum Status Icons?', 'Show icons indicating whether or not a subforum contains new posts or not?  This won''t have any effect unless you enabled subforums display on the index.', 'yesno', '1', 3, 6, 1),
(26, 'hideprivateforums', 'Hide Private Forums?', 'You can hide private forums by turning this option on. This option also hides forums on the forum jump and all subforums.', 'yesno', '1', 4, 6, 1),
(27, 'modlist', 'Forums'' Moderator Listing', 'Here you can turn on or off the listing of moderators for each forum on index.php and forumdisplay.php', 'onoff', '1', 5, 6, 1),
(28, 'showbirthdays', 'Show Today''s Birthdays?', 'Do you want to show today''s birthdays on the forum homepage?', 'yesno', '1', 6, 6, 1),
(29, 'showbirthdayspostlimit', 'Only Show Birthdays with x Posts', 'You can choose to only display users with at least the following amount of posts. A setting of 0 will show all users who have a birthday today.', 'text', '0', 7, 6, 1),
(30, 'showwol', 'Show Who''s Online?', 'Display the currently active users on the forum home page.', 'yesno', '1', 8, 6, 1),
(31, 'showindexstats', 'Show Small Stats Section', 'Do you want to show the total number of threads, posts, members, and the last member on the forum home?', 'yesno', '1', 9, 6, 1),
(32, 'showforumviewing', 'Show x viewing forum', 'Displays the currently active users viewing each forum.', 'yesno', '1', 10, 6, 1),
