(167, 'use_xmlhttprequest', 'Enable XMLHttp request features?', 'This will enable or disable the XMLHttp request features.', 'yesno', '1', 9, 17, 1),
(168, 'useshutdownfunc', 'Use PHP''s Shutdown Functionality', 'This setting for the most part is best left at the default which is detected upon installation. If thread indicators are not updating as well as other meta information, set this setting to ''No''', 'yesno', '0', 9, 17, 1),
(169, 'extraadmininfo', 'Advanced Stats / Debug information', 'Shows Server load, parse time, generation time, Gzip compression, etc on the bottom of all pages in the root folder. Please note that only administrators see this information.', 'yesno', '0', 10, 17, 1),
(170, 'uploadspath', 'Uploads Path', 'The path used for all board uploads. It <b>must be chmod 777</b> (on Unix servers).', 'text', './uploads', 11, 17, 1),
(171, 'useerrorhandling', 'Use Error Handling', 'If you do not wish to use the integrated error handling for MyBB, you may turn this option off. However, it is reccommended that it stay on', 'onoff', '1', 12, 17, 1),
(172, 'errorlogmedium', 'Error Logging Medium', 'The type of the error handling to use.', 'select\r\nnone=Neither\r\nlog=Log errors\r\nemail=Email errors\r\nboth=Log and email errors\r\n', 'none', 13, 17, 1),
(173, 'errortypemedium', 'Error Type Medium', 'The type of errors to show.', 'select\r\nwarning=Warnings\r\nerror=Errors\r\nboth=Warnings and Errors\r\nnone=Hide Errors and Warnings\r\n', 'both', 14, 17, 1),
(174, 'errorloglocation', 'Error Logging Location', 'The location of the log to send errors to, if specified.', 'text', './error.log', 15, 17, 1),
(175, 'enableforumjump', 'Enable Forum Jump Menu?', 'The forum jump menu is shown on the forum and thread view pages. It can add significant load to your forums if you have a large amount of forums. Set to ''No'' to disable it.', 'yesno', '1', 16, 17, 1),
(176, 'ip_forwarded_check', 'Scrutinize User''s IP address?', 'Do you want to check a user''s IP address for HTTP_X_FORWARDED_FOR or HTTP_X_REAL_IP headers? If you''re unsure, set this to no.', 'yesno', '0', 17, 17, 1),
(177, 'postlayout', 'Post Layout', 'Allows you to switch between the classic and new horizontal layout modes. Classic mode shows the author information to the left of the post, horizontal shows the author information above the post.', 'radio\r\nhorizontal=Display posts using the horizontal post layout\r\nclassic=Display posts using the classic layout', 'horizontal', 1, 18, 1),
(178, 'postsperpage', 'Posts Per Page:', 'The number of posts to display per page. We recommend its not higher than 20 for people with slower connections.', 'text', '10', 2, 18, 1),
(179, 'userpppoptions', 'User Selectable Posts Per Page', 'If you would like to allow users to select how many posts are shown per page in a thread, enter the options they should be able to select separated with commas. If this is left blank they will not be able to choose how many posts are shown per page.', 'text', '5,10,20,25,30,40,50', 3, 18, 1),
(180, 'postmaxavatarsize', 'Maximum Avatar Dimensions in Posts', 'The maximum dimensions for avatars when being displayed in a post. If an avatar is too large, it will automatically be scaled down.', 'text', '70x70', 4, 18, 1),
(181, 'threadreadcut', 'Read Threads in Database (Days)', 'The number of days that you wish to keep thread read information in the database. For large boards, we do not recommend a high number as the board will become slower. Set to 0 to disable.', 'text', '7', 5, 18, 1),
(182, 'threadusenetstyle', 'Usenet Style Thread View', 'Selecting yes will cause posts to look similar to how posts look in USENET. No will cause posts to look the modern way.', 'yesno', '0', 6, 18, 1),
(183, 'quickreply', 'Show Quick Reply Form', 'Allows you to set whether or not the quick reply form will be shown at the bottom of threads.', 'onoff', '1', 7, 18, 1),
(184, 'multiquote', 'Show Multi-quote Buttons', 'The multi-quote button allows users to select a series of posts then click Reply and have those posts quoted in their message.', 'onoff', '1', 8, 18, 1),
(185, 'showsimilarthreads', 'Show ''Similar Threads'' Table', 'The Similar Threads table shows threads that are relevant to the thread being read. You can set the relevancy below.', 'yesno', '0', 9, 18, 1),
(186, 'similarityrating', 'Similar Threads Relevancy Rating', 'This allows you to limit similar threads to ones more relevant (0 being not relevant). This number should not be over 10 and should not be set low (<5) for large forums. Does not apply to PgSQL databases.', 'text', '1', 10, 18, 1),
(187, 'similarlimit', 'Similar Threads Limit', 'Here you can change the total amount of similar threads to be shown in the similar threads table. It is recommended that it is not over 15 for 56k users.', 'text', '10', 11, 18, 1),
(188, 'showforumpagesbreadcrumb', 'Show Forum Multipage Dropdown', 'If the forum has more than 1 page of threads, do you want to display a dropdown pagination menu in the breadcrumbs?', 'yesno', '1', 12, 18, 1),
(189, 'browsingthisthread', 'Users Browsing this Thread', 'Here you can turn off the ''users browsing this thread'' feature.', 'yesno', '1', 13, 18, 1),
(190, 'delayedthreadviews', 'Delayed Thread View Updates', 'If this setting is enabled, the number of thread views for threads will be updated in the background by the task schedule system. If not enabled, thread view counters are incremented instantly.', 'onoff', '0', 14, 18, 1),
(191, 'enablewarningsystem', 'Enable Warning System?', 'Set to no to completely disable the warning system.', 'yesno', '1', 1, 19, 1),
(192, 'allowcustomwarnings', 'Allow Custom Warning Types?', 'Allow a custom reason and amount of points to be specified by those with permissions to warn users.', 'yesno', '1', 2, 19, 1),
(193, 'canviewownwarning', 'Can Users View Own Warnings?', 'Set to yes to allow users to view recent warnings in their User CP and show their warning level to them in their profile.', 'yesno', '1', 3, 19, 1),
(194, 'maxwarningpoints', 'Maximum Warning Points', 'The maximum warning points that can be given to a user before it is considered a warning level of 100%.', 'text', '10', 4, 19, 1),
(195, 'wolcutoffmins', 'Cut-off Time (mins)', 'The number of minutes before a user is marked offline. Recommended: 15.', 'text', '15', 1, 20, 1),
(196, 'refreshwol', 'Refresh Who''s online page Time (mins)', 'The number of minutes before the "Who''s online" page refreshes. 0 is disabled.', 'text', '1', 16, 20, 1),
(197, 'enablepruning', 'Enable user pruning?', 'Set to on to prune users by the specified criteria below.', 'onoff', '1', 1, 21, 1),
(198, 'enableprunebyposts', 'Prune user by post count?', 'Set to on to prune users by their number of posts.', 'onoff', '0', 2, 21, 1),
(199, 'prunepostcount', 'Post count to prune by', 'Set to prune users less then a specified number of posts.', 'text', '0', 3, 21, 1),
(200, 'dayspruneregistered', 'Days registered before pruning by post count', 'Set to prune users whom have been registered for atleast the given amount of days.', 'text', '90', 4, 21, 1),
(201, 'pruneunactived', 'Prune unactivated users?', 'Set to on to prune users whom are unactived.', 'onoff', '1', 5, 21, 1),
(202, 'dayspruneunactivated', 'Days registered before pruning unactivated users', 'Set to prune users whom have been unactived for atleast the given amount of days.', 'text', '7', 6, 21, 1),
(203, 'prunethreads', 'Prune User Posts/Threads', 'For the users that are going to be removed, do you want to remove all of their posts/threads?', 'yesno', '1', 7, 21, 1),
(411, 'akismetuidsignore', 'Usergroups to Ignore', 'Usergroups, separated by a comma, to ignore. Use the usergroup id, <strong>not the name</strong>.', 'text', '6,4,3', 4, 51, 0),
(409, 'akismetnumtillban', 'Spam messages until ban', 'The number of spam messages detected by Akismet until the user gets banned (set to 0 to disable).', 'text', '3', 2, 51, 0),
(410, 'akismetfidsignore', 'Forums to Ignore', 'Forums, separated by a comma, to ignore. Use the forum id, <strong>not the name</strong>.', 'text', '', 3, 51, 0),
(208, 'captcha_language', 'ReCAPTCHA Language', 'Which language is used in the interface for the pre-defined themes above.', 'select\nen= English\nnl= Dutch\nfr= French\nde= German\npt= Portugese\nru= Russian\nes= Spanish\ntr= Turkish', 'en', 5, 22, 0),
(407, 'akismetswitch', 'Akismet Main Switch', 'Turns on or off Akismet.', 'onoff', '0', 0, 51, 0),
(408, 'akismetapikey', 'API Key to use for Akismet', 'The API Key used to connect to Akismet. Please check here for more details: <a href="http://wordpress.com/api-keys/" target="_blank">http://wordpress.com/api-keys/</a>', 'text', '', 1, 51, 0),
(217, 'google_seo_404', 'Enable Google SEO 404', 'This module replaces the <i>HTTP 200 OK</i> response with <i>HTTP 404 Not Found</i> for invalid thread / forum / etc error pages and provides additional functionality for 404 error pages. You can also do custom 404 error pages by adding an ErrorPage directive to your .htaccess. Please see the <a href="../inc/plugins/google_seo.html">documentation</a> for details.<br /><br />Set to YES to enable Google SEO 404. Setting this to NO also disables all other settings in the Google SEO 404 settings group.', 'yesno', '1', 1, 25, 0),
(218, 'google_seo_meta', 'Enable Google SEO Meta', 'This module generates meta tags for the current page. Please see the <a href="../inc/plugins/google_seo.html">documentation</a> for details.<br /><br />Set to YES to enable Google SEO Meta. Setting this to NO also disables all other settings in the Google SEO Meta settings group.', 'yesno', '1', 2, 25, 0),
(219, 'google_seo_redirect', 'Enable Google SEO Redirect', 'This module redirects old and invalid URLs to their current proper names. This can be used for all sorts of redirections: redirect to the main site if your forum is available under several domain names, redirect stock MyBB URLs to Google SEO URLs (or the other way around). This prevents your users and Google from seeing the same page under several different names. Please see the <a href="../inc/plugins/google_seo.html">documentation</a> for details.<br /><br />Set to YES to enable Google SEO Redirect. Setting this to NO also disables all other settings in the Google SEO Redirect settings group.', 'yesno', '1', 3, 25, 0),
(220, 'google_seo_sitemap', 'Enable Google SEO Sitemap', 'This module provides <a href="http://sitemaps.org/">XML Sitemap</a> for your forum. This makes it easier for Google to discover pages on your site. Please see the <a href="../inc/plugins/google_seo.html">documentation</a> for details.<br /><br />Set to YES to enable Google SEO Sitemap. Setting this to NO also disables all other settings in the Google SEO Sitemap settings group.', 'yesno', '1', 4, 25, 0),
(221, 'google_seo_url', 'Enable Google SEO URL', 'This module replaces the stock MyBB URLs with descriptive URLs that use words (thread subject, forum title, user name, etc) instead of random numeric IDs. Please see the <a href="../inc/plugins/google_seo.html">documentation</a> for details.<br /><br />Set to YES to enable Google SEO URL. Setting this to NO also disables all other settings in the Google SEO URL settings group.', 'yesno', '1', 5, 25, 0);
INSERT INTO `mybb_settings` (`sid`, `name`, `title`, `description`, `optionscode`, `value`, `disporder`, `gid`, `isdefault`) VALUES
(222, 'google_seo_404_widget', '404 widget', 'Add the Google 404 widget for 404/403 error pages.', 'yesno', '1', 1, 26, 0),
(223, 'google_seo_404_wol_show', 'Show 404 errors in Who''s Online', 'Specify if you want to show that users are seeing the 404 error page in the Who''s Online list. This is not recommended. Enabling this can cause problems such as spambots showing up as guests, or users showing up as seeing error pages if your forum e.g. tries to include an image that does not exist.', 'radio\n0=No\n1=Yes\n2=Yes, including URI', '0', 2, 26, 0),
(224, 'google_seo_404_status', 'Customize HTTP status codes', 'Specify which <a href="http://en.wikipedia.org/wiki/List_of_HTTP_status_codes" target="_blank">HTTP status code</a> should be returned for specific error pages. You can specify one status code per line followed by = and a comma-separated list of error labels, which may include wildcards. By default, the returned status code is 404 Not Found.', 'php\n<textarea name=\\"upsetting[{$setting[''name'']}]\\" rows=\\"5\\" cols=\\"80\\" wrap=\\"off\\">".htmlspecialchars($setting[''value''])."</textarea>', '404 Not Found=*\n403 Forbidden=no_permission\n503 Service Unavailable=boardclosed\n200 OK=nosearchresults,redirect_*', 3, 26, 0),
(225, 'google_seo_404_debug', 'Debug 404 error labels', 'Setting this to Yes will show an error label on error pages. The labels can then be used to configure custom error codes for that page.', 'yesno', '0', 4, 26, 0),
(226, 'google_seo_meta_length', 'Meta description', 'Generate Meta description tags based on the contents of the current page (description of a forum, first posting of a thread, ...). Set to the maximum description length you want to allow or to 0 to disable.', 'text', '200', 1, 27, 0),
(227, 'google_seo_meta_canonical', 'Canonical Page', 'Specify a canonical page. This helps avoid Google indexing the same page under several different names. Please see <a href="http://www.google.com/support/webmasters/bin/answer.py?hl=en&amp;answer=139394">About rel="canonical"</a> for details.', 'yesno', '1', 2, 27, 0),
(228, 'google_seo_meta_archive', 'Meta for Archive Mode', 'Enable this option if you want tags to be added to MyBB''s Lite (Archive) Mode pages. If this setting is disabled, Google SEO Meta will ignore the Archive mode altogether.', 'yesno', '1', 3, 27, 0),
(229, 'google_seo_meta_page', 'Provide page number for forum and thread titles', 'If set, initialize a variable for forum and thread pages &gt; 1. {page} stands for Page (translatable in googleseo.lang.php), {number} for the actual page number. Edit your <i>forumdisplay</i> and <i>showthread</i> templates to include <i>{$google_seo_page}</i> in the &lt;title&gt; tag.', 'text', ' - {page} {number}', 4, 27, 0),
(230, 'google_seo_meta_nofollow', 'Nofollow links', 'Give recently posted links the <a href="http://en.wikipedia.org/wiki/Nofollow" target="_blank">Nofollow</a> attribute. If your forum gets spammed, this will give you time to moderate, while still giving reputation to outgoing links in the long run. The default value is 7.0 days (one week). Set to 0 to disable, or -1 for infinite duration (not recommended).', 'text', '7.0', 5, 27, 0),
(231, 'google_seo_meta_noindex_fids', 'Noindex forums', 'If you want to prevent one or more forums and its threads from being indexed by search engines altogether, enter a comma separated list of forum IDs here. This is not recommended but might be useful for trashcan or duplicate content forums.', 'text', '', 6, 27, 0),
(232, 'google_seo_redirect_permission', 'Permission Checks', 'Should Redirect let permission checks run first? Enabling this option will prevent Redirect from redirecting URLs for items that the user is not allowed to access anyway. This is probably only necessary if you''re also using SEO URLs and you''re concerned about users getting redirected to the SEO URL of a forum / thread they''re not allowed to read, which would give away the subject in the SEO URL.', 'yesno', '0', 1, 28, 0),
(233, 'google_seo_redirect_posts', 'Redirect Post Links', 'MyBB allows linking to specific posts by specifying the post ID (pid) in the URL. Redirect can trust the thread ID (tid) it tid was given and query it if it was not (default MyBB behaviour), or it can verify the tid (by making a query) in order to redirect links to posts that were moved to another thread, or it can ignore these links completely (avoiding the tid query altogether).', 'radio\ndefault=Default (query on demand)\nverify=Verify (always query)\nignore=Ignore (never query)', 'default', 2, 28, 0),
(234, 'google_seo_redirect_litespeed', 'LiteSpeed Bug workaround', 'If your server is running LiteSpeed &lt;= 4.0.10 instead of Apache, and you see redirection loops on member profile / send mail or calendar event / edit event pages, you are suffering from a bug in LiteSpeed''s mod_rewrite replacement. Set to YES to work around this bug - Google SEO Redirect will then leave the problematic pages alone. Apache / Nginx / lighttpd users can leave this at NO.', 'yesno', '0', 3, 28, 0),
(235, 'google_seo_redirect_debug', 'Debug Redirect', 'If you experience infinite redirection loops due to Google SEO Redirect, please enable this option to obtain more information about what is going wrong with your redirect and then report a bug to the plugin author. The debug information is ugly and therefore shown only to board admins.', 'yesno', '0', 4, 28, 0),
(236, 'google_seo_sitemap_url', 'Sitemap URL scheme', 'This is the URL scheme used for the XML Sitemap pages. By default, this is <i>sitemap-{url}.xml</i> and your sitemap will be called <i>sitemap-index.xml</i>. Please note that if you change this, you will also need to add a new rewrite rule to your .htaccess. If your host does not support mod_rewrite, leave this empty. Your sitemap will then be called <i>misc.php?google_seo_sitemap=index</i>.', 'text', 'sitemap-{url}.xml', 1, 29, 0),
(237, 'google_seo_sitemap_forums', 'Forums', 'Include Forums in the XML Sitemap.', 'radio\n0=No\n1=Yes\n2=Yes, including forum pages', '1', 2, 29, 0),
(238, 'google_seo_sitemap_threads', 'Threads', 'Include Threads in the XML Sitemap.', 'radio\n0=No\n1=Yes\n2=Yes, including thread pages', '1', 3, 29, 0),
(239, 'google_seo_sitemap_users', 'Users', 'Include Users in the XML Sitemap.', 'yesno', '1', 4, 29, 0),
(240, 'google_seo_sitemap_announcements', 'Announcements', 'Include Announcements in the XML Sitemap.', 'yesno', '1', 5, 29, 0),
(241, 'google_seo_sitemap_calendars', 'Calendars', 'Include Calendars in the XML Sitemap.', 'yesno', '1', 6, 29, 0),
(242, 'google_seo_sitemap_events', 'Events', 'Include Events in the XML Sitemap.', 'yesno', '1', 7, 29, 0),
(243, 'google_seo_sitemap_additional', 'Additional Pages', 'List of additional URLs relative to your site that should be included in the XML Sitemap. If you have any custom pages you can include them here, one page per line. Entries must be relative to your site, i.e. they must not contain http://, and must not start with .. or /.', 'textarea', 'index.php\nportal.php', 8, 29, 0),
(244, 'google_seo_sitemap_pagination', 'Sitemap Pagination', 'Set the maximum number of items that may appear in a single XML Sitemap before it is split (not counting optional forum/thread pages). Setting it too low will result in too many sitemaps, setting it too high may cause server load every time the sitemap is generated. If unsure, leave at 1000.', 'text', '1000', 9, 29, 0),
(245, 'google_seo_url_query_limit', 'Query Limit', 'Google SEO uses the database to store, and subsequently query, unique SEO URLs for every forum, thread, etc. While these queries are fast and usually low in number, in some cases the total number of queries per request may exceed sane values. Possible causes for this are new installs in large forums when lots of SEO URLs have to be created for the first time, as well as plugins that add lots of unexpected links on a page. Limiting the total number of queries per request helps to avoid load spikes. Stock URLs will appear for the links that couldn''t be queried due to this limit.<p>Set the total number of queries URL is allowed to use in a single request. Default is 50. Set to 0 for no limit.</p>', 'text', '50', 1, 30, 0),
(246, 'google_seo_url_mode', 'Evaluation Mode', 'In Full Mode (which is the default), Google SEO will query and return SEO URLs directly at the time they are requested. This is the most reliable method but it will probably use more than just one database query, depending on how well Google SEO can predict which links will show up on a page. In Lazy Mode, Google SEO will first collect all links created on a page, and only at the very end obtain all SEO URLs in a single query and replace their stock URL counterparts in the output. This reduces queries to a minimum, at the cost of PHP processing time and reliability.', 'radio\n0=Full Mode (Default)\nlazy=Lazy Mode', '0', 2, 30, 0),
(247, 'google_seo_url_cache', 'Use MyBB''s Cache system for SEO URLs', 'If set, the cache will be populated with the URLs used on the specified pages (by default, index.php and portal.php). Under ideal conditions this will reduce the need for additional queries as the required URLs will already be available from the cache. If the setting starts with a number, the cache will be flushed every x minutes (otherwise it defaults to 15 minutes). Set to empty to disable.<p>Warning: Do not include pages which contain unlimited/all URLs like forumdisplay/showthread. They would make the cache grow too large to be useful.</p>', 'text', '15,index.php,portal.php', 3, 30, 0),
(248, 'google_seo_url_punctuation', 'Punctuation characters', 'Punctuation and other special characters are filtered from the URL string and replaced by the separator. By default, this string contains all special ASCII characters including space. If you are running an international forum with non-ASCII script, you might want to add unwanted punctuation characters of those scripts here.', 'text', '!"#$%&''( )*+,-./:;<=>?@[\\]^_`{|}~', 4, 30, 0),
(249, 'google_seo_url_separator', 'URL separator', 'Enter the separator that should be used to separate words in the URLs. By default this is - which is a good choice as it is easy to type in most keyboard layouts (single keypress without shift/alt modifier). If you want some other character or string as a separator, you can enter it here. Please note that special characters like :&amp;?@/ or space could render your URLs unuseable or hard to work with.', 'text', '-', 5, 30, 0),
(250, 'google_seo_url_uniquifier', 'URL uniquifier', 'In case of URL collisions (for example two threads with the same title), the uniquifier is applied to the URL of the newer thread. To guarantee uniqueness, the uniquifier must incorporate the ID and use punctuation other than a single separator. Please see the <a href="../inc/plugins/google_seo.html">documentation</a> for examples of good and bad uniquifiers.', 'text', '{url}{separator}{separator}{id}', 6, 30, 0),
(251, 'google_seo_url_uniquifier_force', 'URL uniquifier enforcer', 'This option is NOT recommended. If you set this to yes, Google SEO will be forced to use the uniquifier for all URLs without exception, even if it''s not necessary. Use this only if you absolutely want every URL to contain the ID. If you enable this, a single separator will be sufficient for the uniquifier.', 'yesno', '0', 7, 30, 0),
(252, 'google_seo_url_translation', 'Character Translation', 'If you want to replace some characters (German umlaut example: Übergrößenträger -&gt; Uebergroessentraeger) or words in your URLs, please add your translations here. You can specify one character per line followed by = and the replacement character. Please see the <a href="../inc/plugins/google_seo.html">documentation</a> for details.', 'php\n<textarea name=\\"upsetting[{$setting[''name'']}]\\" rows=\\"5\\" cols=\\"80\\" wrap=\\"off\\">".htmlspecialchars($setting[''value''])."</textarea>', '', 8, 30, 0),
(253, 'google_seo_url_lowercase', 'lowercase words', 'If you prefer lower case URLs, you can set this to YES. This will not affect the way URLs are stored in the database so you can go back to the original case letters any time. Please note that if you set this to YES, you will also have to make sure that your forum URL, as well as scheme and uniqufier are all lowercase too for the URL to be completely in lower case.', 'yesno', '0', 9, 30, 0),
(254, 'google_seo_url_length_soft', 'URL length soft limit', 'URLs can be shortened after a soft limit by truncating it after a word (punctuation separator). Set to 0 to disable.', 'text', '0', 10, 30, 0),
(255, 'google_seo_url_length_hard', 'URL length hard limit', 'URLs can be shortened after a hard limit by truncating it regardless of word separators. Set to 0 to disable.', 'text', '0', 11, 30, 0),
(256, 'google_seo_url_posts', 'Handle Post Links', 'MyBB allows linking to specific posts by specifying the post ID (pid) in the URL. URL can trust the thread ID (tid) it tid was given and query it if it was not (default), or it can verify the tid (by making a query) in order to show correct links to posts at all times, or it can ignore these links completely (avoiding the tid query altogether by showing a stock post URL).', 'radio\ndefault=Default (query on demand)\nverify=Verify (always query)\nignore=Ignore (never query)', 'default', 12, 30, 0),
(257, 'google_seo_url_multipage', 'Handle multipage links', 'MyBB uses a special multipage() function to produce pagination links for all sorts of things, including forum and thread pages. Set this to Yes to allow Google SEO to attempt replacing these with their SEO URL counterparts, otherwise no.', 'yesno', '1', 13, 30, 0),
(258, 'google_seo_url_forums', 'Forum URL scheme', 'Enter the Forum URL scheme. By default this is <i>Forum-{url}</i>. Please note that if you change this, you will also need to add a new rewrite rule in your .htaccess file. Leave empty to disable Google SEO URLs for Forums.', 'text', 'Forum-{url}', 14, 30, 0),
(259, 'google_seo_url_threads', 'Thread URL scheme', 'Enter the Thread URL scheme. By default this is <i>Thread-{url}</i>. Please note that if you change this, you will also need to add a new rewrite rule in your .htaccess file. Leave empty to disable Google SEO URLs for Threads.', 'text', 'Thread-{url}', 15, 30, 0),
(260, 'google_seo_url_threadprefix', 'Thread Prefixes', 'Include thread prefixes in thread URLs?', 'text', '{prefix}{separator}{url}', 16, 30, 0),
(261, 'google_seo_url_announcements', 'Announcement URL scheme', 'Enter the Announcement URL scheme. By default this is <i>Announcement-{url}</i>. Please note that if you change this, you will also need to add a new rewrite rule in your .htaccess file. Leave empty to disable Google SEO URLs for Announcements.', 'text', 'Announcement-{url}', 17, 30, 0),
(262, 'google_seo_url_users', 'User URL scheme', 'Enter the User URL scheme. By default this is <i>User-{url}</i>. Please note that if you change this, you will also need to add a new rewrite rule in your .htaccess file. Leave empty to disable Google SEO URLs for Users.', 'text', 'User-{url}', 18, 30, 0),
(263, 'google_seo_url_calendars', 'Calendar URL scheme', 'Enter the Calendar URL scheme. By default this is <i>Calendar-{url}</i>. Please note that if you change this, you will also need to add a new rewrite rule in your .htaccess file. Leave empty to disable Google SEO URLs for Calendars.', 'text', 'Calendar-{url}', 19, 30, 0),
(264, 'google_seo_url_events', 'Event URL scheme', 'Enter the Event URL scheme. By default this is <i>Event-{url}</i>. Please note that if you change this, you will also need to add a new rewrite rule in your .htaccess file. Leave empty to disable Google SEO URLs for Events.', 'text', 'Event-{url}', 20, 30, 0),
(265, 'google_seo_url_parent_forum', 'Include parent forum in forum URLs?', 'Set the scheme that should be used to include the parent forum in the forum URL. {parent} is the Google SEO URL of the parent forum, {url} the URL of the forum itself. Enabling this option is not recommended and costs an extra query in the URL creation / verification step for forum URLs.', 'text', '', 21, 30, 0),
(266, 'google_seo_url_parent_thread', 'Include parent forum in thread URLs?', 'Set the scheme that should be used to include the parent forum in the thread URL. {parent} is the Google SEO URL of the parent forum, {url} the URL of the thread itself. Enabling this option is not recommended and costs an extra query in the URL creation / verification step for thread URLs.', 'text', '', 22, 30, 0),
(267, 'google_seo_url_parent_announcement', 'Include parent forum in announcement URLs?', 'Set the scheme that should be used to include parent forum in the announcement URL. {parent} is the Google SEO URL of the parent forum, {url} the URL of the announcement itself. Enabling this option is not recommended and costs an extra query in the URL creation / verification step for announcement URLs.', 'text', '', 23, 30, 0),
