(464, 'myfbconnect_enabled', 'Master switch', 'Do you want to let your users login and register with Facebook? If an user is already registered the account will be linked to its Facebook account.', 'yesno', '1', 1, 58, 0),
(465, 'myfbconnect_appid', 'App ID', 'Enter your App ID token from Facebook Developers site. This will be used together with the secret token to ask authorizations to your users through your app.', 'text', '255368204494205', 2, 58, 0),
(466, 'myfbconnect_appsecret', 'App Secret', 'Enter your App Secret token from Facebook Developers site. This will be used together with the ID token to ask authorizations to your users through your app.', 'text', '7c4d68e89c233c2ac40f1ce3be346a33', 3, 58, 0),
(467, 'myfbconnect_fastregistration', 'One-click registration', 'If this option is disabled, when an user wants to register with Facebook he will be asked for permissions for your app if it''s the first time he is loggin in, else he will be registered and logged in immediately without asking for username changes and what data to sync.', 'yesno', '1', 4, 58, 0),
(468, 'myfbconnect_usergroup', 'After registration usergroup', 'Enter the usergroup ID you want the new users to be when they register with Facebook. By default this value is set to 2, which equals to Registered usergroup.', 'text', '2', 5, 58, 0),
(469, 'myfbconnect_requestpublishingperms', 'Request publishing permissions', 'If this option is enabled, the user will be asked for extra publishing permissions for your application. <b>This option should be left disabled (as it won''t do anything in particular at the moment). In the future it will be crucial to let you post something on the user''s wall when he registers or logins to your board.', 'yesno', '0', 6, 58, 0),
(470, 'myfbconnect_verifiedonly', 'Allow only verified accounts', 'Enable this option to restrict only verified Facebook users to register with Facebook. Verified users are those who confirmed their Facebook registration either with a SMS confirmation or registering with a mobile phone, so they are real people. This option is disabled by default but should be enabled in case you want to ensure that only real people register to your board. Bots won''t be able to register.', 'yesno', '0', 7, 58, 0),
(471, 'myfbconnect_passwordpm', 'Send PM upon registration', 'If this option is enabled, the user will be notified with a PM telling his randomly generated password upon his registration.', 'yesno', '1', 8, 58, 0),
(472, 'myfbconnect_passwordpm_subject', 'PM subject', 'Choose a default subject to use in the generated PM.', 'text', 'New password', 9, 58, 0),
(473, 'myfbconnect_passwordpm_message', 'PM message', 'Write down a default message which will be sent to the registered users when they register with Facebook. {user} and {password} are variables and refer to the username the former and the randomly generated password the latter: they should be there even if you modify the default message. HTML and BBCode are permitted here.', 'textarea', 'Welcome on our Forums, dear {user}!\r\n\r\nWe are pleased you are registering with Facebook. We have generated a random password for you which you should take note somewhere if you would like to change your personal infos. We require for security reasons that you specify your password when you change things such as the email, your username and the password itself, so keep it secret!\r\n\r\nYour password is: [b]{password}[/b]\r\n\r\nWith regards,\r\nour Team', 10, 58, 0),
(474, 'myfbconnect_passwordpm_fromid', 'PM sender', 'Insert the UID of the user who will be the sender of the PM. By default is set to 0 which is MyBB Engine, but you can change it to whatever you like.', 'text', '', 11, 58, 0),
(475, 'myfbconnect_fbavatar', 'Sync avatar and cover', 'If you would like to import avatar and cover from Facebook (and let users decide to sync them) enable this option.', 'yesno', '1', 12, 58, 0),
(476, 'myfbconnect_fbbday', 'Sync birthday', 'If you would like to import birthday from Facebook (and let users decide to sync it) enable this option.', 'yesno', '1', 13, 58, 0),
(477, 'myfbconnect_fblocation', 'Sync location', 'If you would like to import Location from Facebook (and let users decide to sync it) enable this option.', 'yesno', '1', 14, 58, 0),
(478, 'myfbconnect_fblocationfield', 'Location Custom Profile Field ID', 'Insert the Custom Profile Field ID which corresponds to the Location field. Make sure it''s the right ID while you fill it! Default to 1 (MyBB''s default)', 'text', '1', 15, 58, 0),
(479, 'myfbconnect_fbbio', 'Sync biography', 'If you would like to import Biography from Facebook (and let users decide to sync it) enable this option.', 'yesno', '1', 16, 58, 0),
(480, 'myfbconnect_fbbiofield', 'Biography Custom Profile Field ID', 'Insert the Custom Profile Field ID which corresponds to the Biography field. Make sure it''s the right ID while you fill it! Default to 2 (MyBB''s default)', 'text', '2', 17, 58, 0),
(481, 'myfbconnect_fbdetails', 'Sync first and last name', 'If you would like to import first and last name from Facebook (and let users decide to sync it) enable this option.', 'yesno', '0', 18, 58, 0),
(482, 'myfbconnect_fbdetailsfield', 'First and last name Custom Profile Field ID', 'Insert the Custom Profile Field ID which corresponds to the First and last name field. Make sure it''s the right ID while you fill it! Default void (MyBB doesn''t use it)', 'text', '', 19, 58, 0);
