avi a {\r\n float:left; margin-left:9px; text-decoration:none; color:#00bac2; cursor:pointer; font:normal 11px/11px arial\r\n}\r\n\r\n#fbfFooter .footerNavi a:hover {\r\n color:#fff; -moz-transition:color 0.5s ease-in 0s; -o-transition:color 0.5s ease-in; -webkit-transition:color 0.5s ease-in\r\n}\r\n#fbfFooter .fanpage { position:absolute; bottom:15px; left:95px; width:123px; height:28px; padding:10px 0 0 50px; \r\n	background:url(http://forsaken-bastions-fall.com/files/frontend/img/enUS/mainSprite.png) no-repeat 0 -197px;\r\n	font:bold 24px/24px arial; color:#597e86; cursor:pointer; text-decoration:none }\r\n#fbfFooter .fanpage:hover { text-shadow:#a4dfed 0 0 2px; color:#a4dfed; -moz-transition:color 0.5s ease-in 0s; -o-transition:color 0.5s ease-in; -webkit-transition:color 0.5s ease-in  }\r\n/* Partners */\r\n.rocarena { position:absolute; bottom:14px; left:270px; width:35px; height:35px; background:url(http://forsaken-bastions-fall.com/files/frontend/img/enUS/mainSprite.png) no-repeat 0 -235px; }\r\n\r\n.fbLogin {\r\n	position:relative; top:4px; width:18%;\r\n}', 'fbf.css', 1384335878),
